import { initializeApp } from 'firebase/app'
import React, { useState, useEffect } from 'react';
import {
  getFirestore, collection, getDocs
} from 'firebase/firestore'

function Firestore() {

const firebaseConfig = {
  apiKey: "AIzaSyAhQinzfK_Y8YTtNP89Q6xiwYTITLy0-78",
  authDomain: "cpsc-349-16ae4.firebaseapp.com",
  projectId: "cpsc-349-16ae4",
  storageBucket: "cpsc-349-16ae4.appspot.com",
  messagingSenderId: "282632731851",
  appId: "1:282632731851:web:5705801943d17189492f4e",
  measurementId: "G-2J89WM117B"
}

// init firebase app
initializeApp(firebaseConfig);

//init services
const db = getFirestore();

//collection ref
const colRef = collection(db, 'restaurants');

const [data, setData] =  useState([]);

//grab the data
useEffect(() => {
  const fetchData = async () => {
    try {
      const snapshot = await getDocs(colRef);
      setData(snapshot.docs);
    } catch (error) {
      console.error('Error fetching data: ', error);
    }
  };

  fetchData();
}, []);

return (
      <div>
        {data.map((doc) => (
          <div key={doc.id}>
            <div>Name: {doc.name}</div>
            <div>Rating: {doc.rating}</div>
            <div>Hours: {doc.hours}</div>
            <div>Photo: {doc.photo}</div>
            <div>Address: {doc.address}</div>
            <div>Description: {doc.description}</div>
            <div>Price: {doc.price}</div>
          </div>
        ))}
      </div>
  );
}

export default Firestore;
