import Firestore from './Firestore'
import { useState } from 'react';

function Filter(){

  const [selection, setSelection] = useState('All')

  const handleSelection = (event) =>{
    setSelection(event.target.value);
    // console.log(event.target.value)
    // console.log(selection);
  };

  return(
  <div className='filter container'>
    <label>Sort by: </label>
    <input type='radio' name='filter' onChange={handleSelection} value='all'/><label>All</label>
    <input type='radio' name='filter' onChange={handleSelection} value='american'/><label>American</label>
    <input type='radio' name='filter' onChange={handleSelection} value='asian'/><label>Asian</label>
    <input type='radio' name='filter' onChange={handleSelection} value='italian'/><label>Italian</label>
    <input type='radio' name='filter' onChange={handleSelection} value='mexican'/><label>Mexican</label>
    <input type='radio' name='filter' onChange={handleSelection} value='plant based'/><label>Plant Based</label>

    <Firestore 
      selection={selection}
    />
  </div>
 ) 
}

export default Filter
