import { useState } from 'react';

function Filter(){

  const [selection, setSelection] = useState('all')

  const handleSelection = (event) =>{
    setSelection(event.target.value);
    console.log(selection);
  };

  return(
  <div className='filter'>
    <label>Sort by: </label>
    <input type='radio' name='filter' onChange={handleSelection} value='All'/>All
    <input type='radio' name='filter' value='American'/>American
    <input type='radio' name='filter' value='Asian'/>Asian
    <input type='radio' name='filter' value='Italian'/>Italian
    <input type='radio' name='filter' value='Mexican'/>Mexican
    <input type='radio' name='filter' value='Plant Based'/>Plant Based
  </div>
 ) 
}

export default Filter
