import Firestore from './Firestore'
import { useState } from 'react';

function Filter(){

  const [selection, setSelection] = useState()

  const handleSelection = (event) =>{
    setSelection(event.target.value);
    // console.log(event.target.value)
    // console.log(selection);
  };

  return(
  <div>
    <div className='filter container'>
      <label>Sort by: </label>
      <input type='radio' name='filter' onChange={handleSelection} value='' selected checked={selection === ''} checked/><label>All</label>
      <input type='radio' name='filter' onChange={handleSelection} value='american' checked={selection === 'american'}/><label>American</label>
      <input type='radio' name='filter' onChange={handleSelection} value='asian' checked={selection === 'asian'}/><label>Asian</label>
      <input type='radio' name='filter' onChange={handleSelection} value='italian' checked={selection === 'italian'}/><label>Italian</label>
      <input type='radio' name='filter' onChange={handleSelection} value='mexican' checked={selection === 'mexican'}/><label>Mexican</label>
      <input type='radio' name='filter' onChange={handleSelection} value='plant based' checked={selection === 'plant based'}/><label>Plant Based</label>
    </div>
      <Firestore 
        selection={selection}
      />

  </div>
 ) 
}

export default Filter
