import React, { useState } from 'react'
import pic from './download.png';

function Card({name, rating, hours, photo, address, description, price}){
  const [readMore, setReadMore] = useState(false);

  let dollars = '';
  for(let i = 0; i < price; i++){
    dollars += '$';
  }

  let ratingInt = Math.round(rating);
  let stars = '';
  for(let i = 0; i <= 5; i++){

    stars += ratingInt >= i ? `<span className="fa fa-star checked"></span>` : '<span className="fa fa-star"></span>';
  }

  return (

        <div className="card example">
          <div className="card-body">
            <div className="row card-picture">
              <img src={pic} alt="restaurant name" />
            </div>
            <div className="card-text">
              <div className="row">
                <div className="justify-left-right">
                  <label>{name}</label>
                  <div className="rating">
                    {/* <span className="fa fa-star checked"></span>
                    <span className="fa fa-star checked"></span>
                    <span className="fa fa-star checked"></span>
                    <span className="fa fa-star"></span>
                    <span className="fa fa-star"></span> */}
                    {stars}
                  </div>
                </div>
              </div>
              <div className="row">
                <label>{address}</label>
              </div>
              <div className='justify-left-right'>
                <label>{hours}</label>
                <label>{dollars}</label>
              </div>

              <div className="row">
                <label>{readMore ? description : description.substring(0, 200)}</label>
                <button onClick={ () => setReadMore(!readMore)}>
                  {readMore ? 'show less' : 'show more..'}
                </button>
              </div>
            </div>
          </div>
      </div>
  )
}

export default Card
