    const fetchData = async () => {
      try {
        const snapshot = await getDocs(colRef);
        const restaurantData = snapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
        setRestaurants(restaurantData);
      } catch (error) {
        console.error('Error fetching data: ', error);
      }
    };

    fetchData();
  }, []);


return (
