import React, { useState } from 'react'
import { initializeApp } from 'firebase/app'
import { getFirestore, collection, getDocs, addDoc} from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL, getStorage } from 'firebase/storage';

function Add(){

  const firebaseConfig = {
    apiKey: "AIzaSyAhQinzfK_Y8YTtNP89Q6xiwYTITLy0-78",
    authDomain: "cpsc-349-16ae4.firebaseapp.com",
    projectId: "cpsc-349-16ae4",
    storageBucket: "cpsc-349-16ae4.appspot.com",
    messagingSenderId: "282632731851",
    appId: "1:282632731851:web:5705801943d17189492f4e",
    measurementId: "G-2J89WM117B"
  }

  // init firebase app
  initializeApp(firebaseConfig);

  //init services
  const db = getFirestore();

  //collection ref
  const colRef = collection(db, 'restaurants');

  const storage = getStorage();

  const [image, setImage] = useState(null)

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setImage(selectedImage);
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (image) {
      const storageRef = ref(storage, 'images/' + image.name);
      await uploadBytes(storageRef, image);

      // Once the image is uploaded, you can do further processing, such as updating the Firestore database.
    }
  };

  return(
    <div className='o-bg'>
      <form>
        <label>Add Restaurant</label>
        <div className='fields'>
          Name <input className='small-input' />
          Address <input className='small-input' />
          Hours <input className='small-input' />
          Rating <input className='small-input' />
          Price <input className='small-input' />
          Description <input className='textbox'/>
          Type 
          <select className='small-input'>
            <option value='american'>American</option>
            <option value='asian'>Asian</option>
            <option value='italian'>Italian</option>
            <option value='mexican'>Mexican</option>
            <option value='plant based'>Plant Based</option>
          </select>
          {/* <input type="file" onChange={handleImageChange} /> */}
        </div>

      </form>
    </div>
  )
}

export default Add
