import Firestore from './Firestore'
import { useState } from 'react';

function Filter(){

  const [selection, setSelection] = useState('All')

  const handleSelection = (event) =>{
    setSelection(event.target.value);
    // console.log(event.target.value)
    // console.log(selection);
  };

  return(
  <div className='filter'>
    <label>Sort by: </label>
    <input type='radio' name='filter' onChange={handleSelection} value='All'/>All
    <input type='radio' name='filter' onChange={handleSelection} value='American'/>American
    <input type='radio' name='filter' onChange={handleSelection} value='Asian'/>Asian
    <input type='radio' name='filter' onChange={handleSelection} value='Italian'/>Italian
    <input type='radio' name='filter' onChange={handleSelection} value='Mexican'/>Mexican
    <input type='radio' name='filter' onChange={handleSelection} value='Plant Based'/>Plant Based

    <Firestore />
  </div>
 ) 
}

export default Filter
